from flask import Flask, render_template
app = Flask(__name__)

#bind route to view function
@app.route('/') #route
def home():     # view function
    return 'Hello C4E, hihi !!!'


@app.route("/myclass")
def myclass():
    return "asdfghj"

@app.route("/hi/<name>")
def duc(name):
    return "hi " + name

@app.route("/add/<int:num1>/<int:num2>")
def add(num1, num2):
    return str(num1 + num2)

@app.route("/posts")
def post():
  return render_template("post.html")

@app.route("/movie")
def movie():
  return render_template("movie.html", movie = "Joker", img = "data:image/jpeg;base64,")

